import "./App.css";
import { useState } from "react";

function App() {
  const [result, setResult] = useState();
  const [value, setValue] = useState();

  function countAndChangeState() {
    const resultValue = count(value);
    setResult(resultValue);
  }

  const handleInputChange = (e) => {
    setValue(e.target.value);
  };

  function count(number) {
    return 2 * number;
  }

  const getKanbanCardByPhaseId = (data) => {
    const response = fetch(apiRoutes.message, {
      method: "POST",
      headers: {
        Accept: "application/json, text/plain",
        "Content-type": "application/json",
      },
      body: JSON.stringify(data),
    }).then((res) => {
      return res.json();
    });
    return response;
  };

  return (
    <div className="App">
      number:
      <input type="text" onChange={handleInputChange} />
      <button onClick={countAndChangeState}>Press me to calculate</button>
      <textarea value={result} readOnly></textarea>
    </div>
  );
}

export default App;
